mapper.select:


SELECT * FROM ( 
       SELECT ROWNUM AS RNUM, A.* FROM (
                SELECT
            board_id, b_title, b_content, board_type_id, b_date
                  FROM
                barny_board 
    where





    board_type_id = 0 order by board_id desc                  
    ) A WHERE ROWNUM <= #{pageNum} * #{amount}
  ) WHERE RNUM > (#{pageNum}-1) * #{amount}
